import { Form, Input, Button, Checkbox, notification } from "antd";
import { connect } from 'react-redux';
import { Link } from "react-router-dom";

import history from '../../utils/history';

import { loginAction } from '../../redux/actions';

function LoginPage({ userList, login }) {
  const [loginForm] = Form.useForm();
  function handleSubmit(values) {
    const userInfo = userList.find((user) => {
      return user.email === values.email && user.password === values.password;
    })
    if (userInfo) {
      localStorage.setItem('userInfo', JSON.stringify(userInfo));
      login(userInfo);
      if (userInfo.role === 'user') {
        history.push('/');
      } else {
        history.push('/admin')
      }
    } else {
      loginForm.setFields([
        {
          name: 'email',
          errors: [' ']
        },
        {
          name: 'password',
          errors: ['Email hoặc mật khẩu không đúng!']
        }
      ]);
    }
  }

  return (
    <div className="login-container">
      <div className="login-form">
        <div className="login-title">
          <h2>Login</h2>
        </div>
        <Form
          form={loginForm}
          name="basic"
          layout="vertical"
          initialValues={{ remember: true }}
          onFinish={(values) => handleSubmit(values)}
        >
          <Form.Item
            label="Email"
            name="email"
            rules={[
              { required: true, message: "Bạn chưa nhập email!" },
              { type: 'email', message: "Email không hợp lệ!" },
            ]}
          >
            <Input />
          </Form.Item>

          <Form.Item
            label="Mật khẩu"
            name="password"
            rules={[{ required: true, message: "Bạn chưa nhập tài khoản!" }]}
          >
            <Input.Password />
          </Form.Item>

          <Form.Item
            name="remember"
            valuePropName="checked"
          >
            <Checkbox>Ghi nhớ tài khoản</Checkbox>
          </Form.Item>

          <div style={{ display: 'inline-block', marginBottom: 16 }}>
            Bạn chưa có tài khoản?&nbsp;
            <Link to="/register">
              Bấm vào đây để đăng ký
            </Link>
          </div>

          <Button type="primary" htmlType="submit" block>
            Đăng nhập
          </Button>
        </Form>
      </div>
    </div>
  );
}

const mapStateToProps = (state) => {
  const { userList } = state.userReducer;
  return {
    userList: userList,
  };
}

const mapDispatchToProps = (dispatch) => {
  return {
    login: (params) => dispatch(loginAction(params)),
  }
};

export default connect(mapStateToProps, mapDispatchToProps)(LoginPage)
