import {TASK_LIST_ACTION} from '../constants';
import {createReducer} from "@reduxjs/toolkit";

const initialState = localStorage.tasks && JSON.parse(localStorage.tasks) || {taskList: []};

const saveLocal = (taskList) => {
    localStorage.tasks = JSON.stringify(taskList);
}
const taskListReducer = createReducer(initialState, {
    [TASK_LIST_ACTION.ADD_TASK]: (state, action) => {
        let task = {
            ...state,
            taskList: [
                ...state.taskList,
                action.payload,
            ]
        };
        saveLocal(task);
        return task;
    },
    [TASK_LIST_ACTION.EDIT_TASK]: (state, {payload}) => {
        let tasks = {...state};
        let taskIndex = tasks.taskList.findIndex(task => task.id === payload.id);
        tasks.taskList.splice(taskIndex, 1, payload);
        saveLocal(tasks);
        return tasks;
    },
    [TASK_LIST_ACTION.DELETE_TASK]: (state, action) => {
        let task = {...state};
        task.taskList.splice(action.payload.index, 1);
        saveLocal(task);
        return task;
    },
});

export default taskListReducer;
