import {TASK_LIST_ACTION} from '../constants';
import {createReducer} from "@reduxjs/toolkit";

const initialState = localStorage.task && JSON.parse(localStorage.task) || {taskList: []};

const saveLocal = (taskList) => {
    localStorage.task = JSON.stringify(taskList);
}

const taskListReducer = createReducer(initialState, {
    [TASK_LIST_ACTION.ADD_TASK]: (state, action) => {
        let task = {
            ...state,
            taskList: [
                ...state.taskList,
                action.payload,
            ]
        };
        saveLocal(task);
        return task;
    },
    [TASK_LIST_ACTION.EDIT_TASK]: (state, action) => {
        console.log(action)
        let task = {...state};
        task.taskList.splice(action.payload.index, 1, action.payload.value);
        saveLocal(task);
        return task;
    },
    [TASK_LIST_ACTION.DELETE_TASK]: (state, action) => {
        let task = {...state};
        task.taskList.splice(action.payload.index, 1);
        saveLocal(task);
        return task;
    },
});

export default taskListReducer;
